function executeSkill(context) { 
  const caster = context.piece;
  const targetEnemy = selectTarget({ type: 'piece', range: 5, filter: 'enemy' });
  if (!targetEnemy || targetEnemy.needsTargetSelection) {
    return targetEnemy;
  }
  // 计算伤害
  const damage = Math.round(caster.attack * context.skill.powerMultiplier);
  const damageResult = dealDamage(caster, targetEnemy, damage, 'physical', context.battle, context.skill.id);
  if (damageResult.success) {
    addStatusEffectById(targetEnemy.instanceId, 'bleeding');
  }
  return { message: caster.templateId + '使用出血之刃，对' + targetEnemy.templateId + '造成' + damage + '点伤害，并施加了流血状态', success: true };
}
