function executeSkill(context) { 
  const caster = sourcePiece;
  const nearestEnemy = select.getNearestEnemy();

  if (nearestEnemy) {
    // 计算伤害
    const damage = Math.round(caster.attack * context.skill.powerMultiplier);
    nearestEnemy.currentHp = Math.max(0, nearestEnemy.currentHp - damage);

    // 施加流血状态
    addBleeding(nearestEnemy, 3, 5);

    return { message: caster.templateId + '使用出血之刃，对' + nearestEnemy.templateId + '造成' + damage + '点伤害，并施加了流血状态', success: true };
  }
  return { message: '范围内没有可攻击的敌人', success: false };
}
