function executeSkill(context) {
  const reaper = context.piece;
  const victim = context.targetPiece;
  if (victim) {
    const soulPower = victim.maxHp;
    // 增加最大生命值
    reaper.maxHp += soulPower;
    // 增加当前生命值
    reaper.currentHp += soulPower;
    return { message: reaper.templateId + '从' + victim.templateId + '的死亡中汲取力量，生命值增加' + soulPower, success: true };
  }
  return { message: '没有灵魂可以收割', success: false };
}
