function EffectTrigger(context) { const piece = context.piece; const nearbyEnemies = context.battleState.pieces.filter(enemy => {
    if (enemy.playerId === piece.playerId || enemy.currentHp <= 0) return false;
    const distance = Math.abs(enemy.x - piece.x) + Math.abs(enemy.y - piece.y);
    return distance <= 5;
  });
  nearbyEnemies.forEach(enemy => {
    const damage = piece.attack;
    enemy.currentHp = Math.max(0, enemy.currentHp - damage);
  });
  return { success: true, message: piece.templateId + '的白骨风暴对周围敌人造成伤害' }; }
